SELECT  ?site ?sitename ?visitors
WHERE
  { SERVICE <http://pyravlos1.di.uoa.gr:8080/geoqa/Query> {
            ?manchester owl:sameAs <http://yago-knowledge.org/resource/Manchester> ;
               geo:hasGeometry [ geo:asWKT ?manchesterWKT ] .
                    {
                        { ?site a osm:Attraction . }
                            UNION
                        { ?site a osm:Museum . }
                    }
                ?site  owl:sameAs ?link ;
                  osm:has_name ?sitename ;
                  geo:hasGeometry [ geo:asWKT ?siteWKT ] .
            FILTER(geof:sfWithin(?siteWKT, ?manchesterWKT)) .
        }
        SERVICE <http://pyravlos1.di.uoa.gr:8890/sparql> {
            ?link <http://yago-knowledge.org/resource/infobox/en/visitors> ?visitors  .
        }
}
ORDER BY DESC(?visitors)
LIMIT 1

