SELECT  ?district1name (count(?district2) as ?count)           
WHERE
  {  SERVICE <http://pyravlos1.di.uoa.gr:8080/geoqa/Query> {
            ?district1  a <http://kr.di.uoa.gr/yago2geo/ontology/OS_UnitaryAuthority> ;
                        gadm:hasName ?district1name ;
                        geo:hasGeometry [ geo:asWKT ?district1WKT ] .
            ?district2  a <http://kr.di.uoa.gr/yago2geo/ontology/OS_UnitaryAuthority> ;
                        gadm:hasName ?district2name ;
                        geo:hasGeometry [ geo:asWKT ?district2WKT ] .
            filter( (geof:sfTouches(?district1WKT, ?district2WKT))  &&
                      (?district1 != ?district2) ).
        }
}
GROUP BY ?district1name
ORDER BY DESC(?count)
LIMIT 1
